'use strict';

function cusomeBy(arr, n, predicate) {
    var out = new Array(arr.length);
    var count = 0;

    for (var i = 0; i < arr.length; i++) {
        if (predicate(arr[i])) {
            count++;
        }
        out[i] = count >= n;
    }

    return out;
}

cusomeBy.assign = function(arr, n, out, stride, offset, predicate) {
    var count = 0;

    for (var i = 0; i < arr.length; i++) {
        if (predicate(arr[i])) {
            count++;
        }
        out[offset + i * stride] = count >= n;
    }

    return out;
};

module.exports = cusomeBy;
